import React, { Component } from "react";
import { Link } from "react-router-dom";
import './index.css';

export default class Main extends Component {
    constructor(props) {
        super(props);

        this.state = {
            aluno: [],
            erro: null
        };
    }
    componentDidMount() {
        fetch('https://aluno-backend-77e363af099c.herokuapp.com/aluno')
        .then(aluno =>
            aluno.json().then(aluno => this. setState({ aluno }))
            )
            .catch(erro => this.setState({ erro }));

    }

    render() {
        const { aluno } = this.state;
        return aluno.map((aluno, index) => (
            <div className="aluno-list">
              <div key={index} className="card mb-4">
                <h5 className="card-header">{aluno.nome}</h5>

                <article key={aluno._id}>
                    <strong> {aluno.ra}</strong>
                    <p> <Link to={'/aluno/${aluno.id}'}> Acessar</Link></p>

                    <br />
                </article>
                </div>  
                </div>
        ))

    };
}
